// HNI segment LinkedIn search queries
const queries = [
  {
    "title": "CEO",
    "location": "Mumbai, India",
    "industry": "Technology",
    "companySize": "51-500",
    "education": "IIT OR IIM"
  },
  {
    "title": "Founder",
    "location": "Bangalore, India",
    "industry": "Technology OR E-commerce",
    "companySize": "51-500",
    "education": "IIT OR IIM"
  },
  {
    "title": "CFO",
    "location": "Delhi NCR, India",
    "industry": "Manufacturing OR FMCG",
    "companySize": "51-1000"
  },
  {
    "title": "Managing Director",
    "location": "Pune, India",
    "industry": "Healthcare OR Pharmaceuticals",
    "companySize": "51-500"
  },
  {
    "title": "Director",
    "location": "Hyderabad, India",
    "industry": "Technology OR Consulting",
    "companySize": "51-1000"
  },
  {
    "title": "Vice President",
    "location": "Chennai, India",
    "industry": "Banking OR Financial Services",
    "companySize": "500-5000"
  }
];

return queries.map(q => ({ json: q }));

// Lead scoring for HNI segment
const lead = $input.item.json;

let score = 0;

// Net worth indicators (0-30 points)
let netWorthIndicators = 0;
if (lead.company && lead.companySize?.includes('500-5000')) netWorthIndicators++;
if (lead.title && (lead.title.includes('CEO') || lead.title.includes('Founder') || lead.title.includes('CFO'))) netWorthIndicators++;
if (lead.education && (lead.education.includes('IIT') || lead.education.includes('IIM') || lead.education.includes('Harvard') || lead.education.includes('Stanford'))) netWorthIndicators++;
if (lead.location && (lead.location.includes('Mumbai') || lead.location.includes('Delhi') || lead.location.includes('Bangalore'))) netWorthIndicators++;

if (netWorthIndicators >= 3) score += 30;
else if (netWorthIndicators === 2) score += 20;
else if (netWorthIndicators === 1) score += 10;

// Role level (0-20 points)
if (lead.title?.match(/CEO|Founder|CFO|Managing Director/i)) score += 20;
else if (lead.title?.match(/VP|Vice President|Director/i)) score += 15;
else if (lead.title?.match(/Senior Manager|Manager/i)) score += 10;

// Education (0-15 points)
if (lead.education?.match(/IIM|IIT|Harvard|Stanford|Wharton|Oxford|Cambridge/i)) score += 15;
else if (lead.education?.match(/Top B-School|Foreign University/i)) score += 10;
else score += 5;

// LinkedIn engagement (0-15 points)
if (lead.posts > 20 || lead.activityLevel === 'very_high') score += 15;
else if (lead.posts > 10 || lead.activityLevel === 'high') score += 10;
else if (lead.posts > 5 || lead.activityLevel === 'medium') score += 5;

// Age range (0-10 points) - estimate from experience years
const experienceMatch = lead.experience?.match(/(\d+)\+?\s*years?/i);
if (experienceMatch) {
  const experience = parseInt(experienceMatch[1]);
  const estimatedAge = 22 + experience; // Assuming graduation at 22
  if (estimatedAge >= 40 && estimatedAge <= 55) score += 10;
  else if ((estimatedAge >= 35 && estimatedAge < 40) || (estimatedAge > 55 && estimatedAge <= 60)) score += 7;
  else score += 3;
}

// Location (0-10 points)
if (lead.location?.match(/Mumbai|Delhi NCR|Bangalore/i)) score += 10;
else if (lead.location?.match(/Pune|Hyderabad|Chennai/i)) score += 7;
else score += 5;

// Return enriched lead with score
return {
  json: {
    ...lead,
    leadScore: score,
    leadTier: score >= 80 ? 'Hot' : score >= 60 ? 'Warm' : 'Cold',
    segment: 'HNI',
    scrapedDate: new Date().toISOString()
  }
};

const items = $input.all();
const totalLeads = items.length;
const hotLeads = items.filter(item => item.json.leadScore >= 80).length;
const warmLeads = items.filter(item => item.json.leadScore >= 60 && item.json.leadScore < 80).length;

const summary = {
  totalScraped: totalLeads,
  qualified: hotLeads + warmLeads,
  hot: hotLeads,
  warm: warmLeads,
  videosGenerated: hotLeads,
  emailsSent: hotLeads,
  scrapeDate: new Date().toISOString(),
  segment: 'HNI'
};

return [{ json: summary }];

